double triangleArea(double a, double b, double c)
{
    // 根据海龙公式，首先计算半周长
    double s = (a + b + c) / 2.0;

    // 计算面积
    double area = sqrt(s * (s - a) * (s - b) * (s - c));

    return area;
}
